var buildFormattingTokensRegExp = require('date-fns/locale/_lib/build_formatting_tokens_reg_exp')

function buildFormatLocale () {
  var months3char = ['Tháng Giêng', 'Tháng Hai', 'Tháng Ba', 'Tháng Tư', 'Tháng Năm', 'Tháng Sáu', 'Tháng Bảy', 'Tháng Tám', 'Tháng Chín', 'Tháng Mười', 'Tháng Mười Một tháng Mười Hai']
  var monthsFull = ['Tháng Giêng', 'Tháng Hai', 'Tháng Ba', 'Tháng Tư', 'Tháng Năm', 'Tháng Sáu', 'Tháng Bảy', 'Tháng Tám', 'Tháng Chín', 'Tháng Mười', 'Tháng Mười Một tháng Mười Hai']
  var weekdays2char = ['Chủ nhật', 'Thứ Hai', 'Thứ Ba', 'Thứ tư', 'Thứ năm', 'Thứ sáu', 'Ngày thứ bảy']
  var weekdays3char = ['Chủ nhật', 'Thứ Hai', 'Thứ Ba', 'Thứ tư', 'Thứ năm', 'Thứ sáu', 'Ngày thứ bảy']
  var weekdaysFull = ['Chủ nhật', 'Thứ Hai', 'Thứ Ba', 'Thứ tư', 'Thứ năm', 'Thứ sáu', 'Ngày thứ bảy']
  var meridiemUppercase = ['Buổi sáng', 'Chiều']
  var meridiemLowercase = ['Buổi sáng', 'Chiều']
  var meridiemFull = ['Buổi sáng', 'Chiều']

  var formatters = {
    // Month: Jan, Feb, ..., Dec
    'MMM': function (date) {
      return months3char[date.getMonth()]
    },

    // Month: January, February, ..., December
    'MMMM': function (date) {
      return monthsFull[date.getMonth()]
    },

    // Day of week: Su, Mo, ..., Sa
    'dd': function (date) {
      return weekdays2char[date.getDay()]
    },

    // Day of week: Sun, Mon, ..., Sat
    'ddd': function (date) {
      return weekdays3char[date.getDay()]
    },

    // Day of week: Sunday, Monday, ..., Saturday
    'dddd': function (date) {
      return weekdaysFull[date.getDay()]
    },

    // AM, PM
    'A': function (date) {
      return (date.getHours() / 12) >= 1 ? meridiemUppercase[1] : meridiemUppercase[0]
    },

    // am, pm
    'a': function (date) {
      return (date.getHours() / 12) >= 1 ? meridiemLowercase[1] : meridiemLowercase[0]
    },

    // a.m., p.m.
    'aa': function (date) {
      return (date.getHours() / 12) >= 1 ? meridiemFull[1] : meridiemFull[0]
    }
  }

  // Generate ordinal version of formatters: M -> Mo, D -> Do, etc.
  var ordinalFormatters = ['M', 'D', 'DDD', 'd', 'Q', 'W']
  ordinalFormatters.forEach(function (formatterToken) {
    formatters[formatterToken + 'o'] = function (date, formatters) {
      return ordinal(formatters[formatterToken](date))
    }
  })

  return {
    formatters: formatters,
    formattingTokensRegExp: buildFormattingTokensRegExp(formatters)
  }
}

function ordinal (number) {
  return number + 'ngày'
}

module.exports = buildFormatLocale
